    def se(self):
        cp = configparser.ConfigParser()
        cp.read('sp.ini')
        conn = cp.get('com', 'serialPort')
        global s
        try:
            s= serial.Serial(conn, 9600, timeout=0.5)
            # s = serial.Serial("/dev/tty.usbserial-1710", 9600, timeout=0.5)
        except Exception as e:
            self.jy()
            print(e)

    def wt(self, n):
        try:
            s.write(n)
        except Exception as e:
            self.jy()
            print("could not open serial port!", e)

    def st(self):
        d = bytes.fromhex('00')
        self.wt(d)

    def ffa(self):
        d = bytes.fromhex('56')
        self.wt(d)

    def ffb(self):
        d = bytes.fromhex('65')
        self.wt(d)

    def iia(self):
        d = bytes.fromhex('34')
        self.wt(d)

    def iib(self):
        d = bytes.fromhex('43')
        self.wt(d)

    def uup(self):
        d = bytes.fromhex('12')
        self.wt(d)

    def ddo(self):
        d = bytes.fromhex('21')
        self.wt(d)

    def lle(self):
        d = bytes.fromhex('78')
        self.wt(d)

    def rri(self):
        d = bytes.fromhex('87')
        self.wt(d)

